<?php namespace NextAddons\Modules\Controls;use \Elementor\Controls_Manager;use \Elementor\Element_Base;defined('ABSPATH')||exit;class Link{private static $instance;public function __init(){add_action('elementor/element/column/section_advanced/after_section_end',[$this,'__add_content'],1);add_action('elementor/element/common/_section_style/after_section_end',[$this,'__add_content'],1);add_action('elementor/element/section/section_advanced/after_section_end',[$this,'__add_content'],1);add_action('elementor/frontend/before_render',[$this,'__add_content_render'],1);}public function __add_content_render(Element_Base $el){$settings=$el->get_settings_for_display();$link=isset($settings['next_link_render'])?$settings['next_link_render']:'';$enable=isset($settings['next_link_on_render'])?$settings['next_link_on_render']:'no';if($link&&!empty($link['url'])&&$enable=='yes'){$el->add_render_attribute('_wrapper',['data-nx-link'=>json_encode($link),'style'=>'cursor: pointer','class'=>'nx-wpperlink-render']);}}public function __add_content(Element_Base $el){$tabs=Controls_Manager::TAB_CONTENT;if('section'===$el->get_name()||'column'===$el->get_name()){$tabs=Controls_Manager::TAB_LAYOUT;}$el->start_controls_section('_section_next_link',['label'=>__('Next Link','next-addons'),'tab'=>$tabs,]);$el->add_control('next_link_on_render',['label'=>esc_html__('Enable','next-addons'),'type'=>Controls_Manager::SWITCHER,'label_on'=>esc_html__('On','next-addons'),'label_off'=>esc_html__('Off','next-addons'),'return_value'=>'yes','default'=>'no',]);$el->add_control('next_link_render',['label'=>__('Link','next-addons'),'type'=>Controls_Manager::URL,'dynamic'=>['active'=>true,],'placeholder'=>__('https://example.com','next-addons'),'condition'=>['next_link_on_render'=>'yes']]);$el->end_controls_section();}public static function instance(){if(!self::$instance){self::$instance=new self();}return self::$instance;}}